(1, 1, 'A WordPress Commenter', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2018-06-08 22:47:16', '2018-06-08 22:47:16', 'Hi, this is a comment.\nTo get started with moderating, editing, and deleting comments, please visit the Comments screen in the dashboard.\nCommenter avatars come from <a href=\"https://gravatar.com\">Gravatar</a>.', 0, '1', '', '', 0, 0),
(2, 11, 'admin', 'admin@admin.com', '', '::1', '2018-06-10 20:16:20', '2018-06-10 20:16:20', 'This is a comment.', 0, '1', 'Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/67.0.3396.79 Safari/537.36', '', 0, 1),
(3, 11, 'admin', 'admin@admin.com', '', '::1', '2018-06-10 20:29:24', '2018-06-10 20:29:24', 'This is also a comment', 0, '1', 'Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/67.0.3396.79 Safari/537.36', '', 0, 1);
